import React from "react";
import './Button.css';

const STYLES = [
    'btn--primary',
    'bnt--outline'
]

const SIZES = [
    'btn--medium',
    'btm--large'
]

export const Button = ({
    children, 
    type,
    onClick,
    buttonStyle,
    buttonSize
}) => {
    const checkButtonStyle = STYLES.includes(buttonStyle) ? buttonStyle : STYLES[0]

    const checkButtonSize = SIZED.includes(buttonSize) ? buttonSize : SIZES[0]

    return(
        <button className={`btn ${checkButtonStyle} ${checkButtonSize}`} type={type}>
            {children}
        </button>
    )
}
